<template><div v-if="typeof (item) === 'object'"> 
<div class="item-box"> 
	<div class="info-box">
		<div class="cover-box">
			<img :src="(load_img === '') ? default_product : load_img" :onerror="'this.src=\''+default_product+'\''"/></div>
			<img :src="splitImg(item['image-url'])" v-on:load="load_img=splitImg(item['image-url'])" style="display:none">
		<div class="infomation-box">
			<div class="item-info-title-warp">
				<div class="item-info-title">{{item['item-name']}}</div> 
				<div class="item-redeem-warp">
					<span class="item-redeem-icon"><img :src="treat_icon" alt=""></span>
					<span class="item-redeem-treatnum">{{item['treats_worth']}}</span>
				</div>
			</div>
			<div class="item-info-price-warp"> 
				<span class="item-info-newprice" v-html="each(item['discount_price'])"></span>
				<span class="item-info-oldprice" >{{item['price'] === '' ? '' : item['price'] }}</span>
			</div>
		</div>
	</div>
	<div class="promotion-description-box">
		<span class="item-info-typename">Promotion description:</span><br>{{item['promotion_description']}}
	</div>
	<div class="item-info-date-warp">
		<div class="">
			<div class="item-info-typename">Redeem date </div> 
			<div class="item-info-date">{{formatDate(item['redeem_date'])}}</div>
		</div>
		<div class="">
			<div class="item-info-typename">Expiration date </div> 
			<div class="item-info-date">{{formatDate(item['end_date'])}}</div>
		</div>
	</div> 
	<div class="item-info-date-procode">  
		<div class="item-info-typename">Promotion code:</div> 
		<div class="item-info-code">
			<div class="code-warp">{{item['promotion_code'].split('-')[0]}}</div>
			<div class="code-line">-</div>
			<div class="code-warp">{{item['promotion_code'].split('-')[1]}}</div>
			<div class="code-line">-</div>
			<div class="code-warp">{{item['promotion_code'].split('-')[2]}}</div>
		</div> 
	</div>
	<div class="buttons-layout">  
		<button class="history-copy-code-button" :id="'copyButton'+item['promotion_code']"  :data-clipboard-target="'#code'+item['promotion_code']" v-on:click="showAlert" >Copy code</button>
		<button class="button4" style="margin-left:10px;" @click="amazonGo(item['asin'])">Buy on Amazon</button> 
	</div>
</div>
<input :id="'code' + item['promotion_code']" v-model="item['promotion_code']" style="opacity:0;position: fixed;left: -10000px;top:-10000px;">
</div></template>
	
<script>
	import common from '../common/common.js';
	import publicConf from '../common/public.conf.js';

	const baseUrl = publicConf.currentBaseUrl;

	export default {
		props : ['item'],

		data () { 
			return {
				 formatDate : common.formatDate,
				 treat_icon : baseUrl + "treat-icon.png",
				 load_img : '',
				 default_product : "data:image/png;base64,",
			}
		},

		mounted () {
			new Clipboard('#copyButton'+this.item['promotion_code']);

			$('#code' + this.item['promotion_code']).focus(function() {
				setTimeout(function () {
					window.document.activeElement.blur();
				}, 100);
			});

		},

		methods : {
			
			splitImg (urls){
				if (urls == null) {
					return this.default_product; 
				}
				return urls.split(",")[0];
			},

			cutoffStr (str) { 
				if ( str.length >= 85 ) { 
					return str.substring(0,85) + "...";
				} else {
					return str;
				}
			},

			each (str) {
				var strArr = str.split(" ");
				if (strArr[1] === 'each' ) {
					return strArr[0] + "&nbsp;<span style='font-size:12px;'>each</span>"
				} else {
					return str;
				}
			},

			showAlert () { 
			    this.$emit("showAlert");  
			},
		},
		
		components: { 
		}
	}
</script>

