<template>
	<div class="item-warp" :class="{'item-only-one' : count === 1 , 'marginl' : index % 2 === 1 && count > 1 }">
		<div class="item-cover" @click="go('/Deatail?userId='+ userId + '&deatailId=' + item['product-id'] + '&pid=' + item['pid'] )" >
			<img :src="(load_img === '') ? default_product : load_img" alt="" :onerror="'this.src=\''+default_product+'\''"/>
			<img :src="splitImg(item['image-url'])" v-on:load="load_img=splitImg(item['image-url'])" style="display:none">
		</div>
		<div class="item-body" @click="go('/Deatail?userId='+ userId + '&deatailId=' + item['product-id'] + '&pid=' + item['pid'] )" >
			<div class="item-title">
				{{item['item-name']}}
			</div>
			<div class="item-price">
				<span class="item-price-new" v-html="each(item['discount_price'])"></span> 
				<span class="item-price-old">{{item['price'] === '' ? '' :  item['price']}}</span>
			</div>
			<div class="item-promotion-warp">
				<span v-if="item['tag_is_show'] === 1" class="item-promotion" :id="'promotion' + item['product-id']" >
					{{item['tag']}}&nbsp;
					<div class="triangle" ></div> 
				</span>
				<br v-if="item['tag_is_show'] === 1" />
				<span class="item-promotion-noback" v-if="item['astrict'] !== ''">
					{{item['astrict']}}
				</span>
			</div>
		</div>
		<div class="item-foot">
			<div class="item-hr"></div>
<!-- 			<div class="item-read-more"> 
				<button class="read-more-button" >Read more ></button>
			</div> -->
			<div class="item-redeem">
				<div class="">
					<div style="height:20px;">
						<span class="item-redeem-icon"><img :src="treat_icon" alt=""></span>
						<span class="item-redeem-treatnum">{{item['treats_worth']}}</span>
					</div>
					<div>
						<div class="item-leftnum">{{item['left']}} left</div>
					</div>
				</div>
				<button :class="{ 
							'small-redeem-button' :  Number(userTRNum) >= Number(item['treats_worth']) , 
							'small-redeem-unavailable-button' :  Number(userTRNum) < Number(item['treats_worth']) 
						}" 
						@click="showprompt" >
						Redeem
				</button>
			</div>
		</div>
	</div>
</template>

<script>
	import publicConf from '../common/public.conf.js';
	import common from '../common/common.js';
	
	
	const baseUrl = publicConf.currentBaseUrl;

	export default {
		props : ['item', 'index', 'userTRNum', 'count'],

		data () { 
			return {
				treat_icon : baseUrl + "treat-icon.png",
				default_product : "data:image/png;base64,",
				load_img : '',
				userId : ''
			}
		},

	    beforeMount () { 
	        this.userId = common.GET()['userId'];
	        console.log(this.item);
	    },

	    mounted () {   
	    	var tags = $(".item-promotion");
	    	
	    	for (var i = 0; i < tags.length; i++) {
	    		let triangle = $(tags[i]).find('.triangle'); 
	    		let tagHeight = $(tags[i]).height();

	    		$(triangle).css("border-top",   (tagHeight / 2) + "px solid transparent");
	    		$(triangle).css("border-bottom", (tagHeight / 2) + "px solid transparent"); 
	    		$(triangle).css("left", $(tags[i]).width() + 16 + "px");
	    		if ( tagHeight === 20 ) { 
	    			$(triangle).css("top", "3px");
	    		}
	    	}
	    },

		methods : {
			showprompt () {
				if ( Number(this.userTRNum) >= Number(this.item['treats_worth']) ) {
					this.$emit("showprompt",this.item);
				}
			},

			splitImg (urls){
				if (urls == null) {
					return this.default_product; 
				}
				return urls.split(",")[0];
			},

			each (str) { 
				var strArr = str.split(" ");
				if (strArr[1] === 'each' ) {
					return strArr[0] + "&nbsp;<span style='font-size:12px;'>each</span>"
				} else {
					return str;
				}
			}		 
		},

		components: {

		}
	}
</script>

