<template>
<div class="screen">
	<div class="prompt" v-if="prompt1"> 
		<div class="treats-info">
			<div class="treats-info-title">{{goods['item-name']}}</div>
			<div class="treats-info-cover"> 
				<img :src="(load_img === '') ? default_product : load_img" :onerror="'this.src=\''+default_product+'\''"/>
				<img :src="getGoodUrl(goods)" v-on:load="load_img=getGoodUrl(goods)" style="display:none">
			</div>
			
			<div class="item-proprice-warp">

				<div class="item-info-price-warp">   
				    <span class="item-info-newprice" v-html="each(goods['discount_price'])"></span>
				    <span class="item-info-oldprice" v-if="goods['price'] !== ''">{{goods['price'] === '' ? '&nbsp;' :  goods['price']}}</span>
				</div>

				<div class="treats-info-data-img">
					<img v-bind:src="treat_icon" alt="">&nbsp;<span class="treats-info-data-num">{{goods['treats_worth']}}</span>
				</div>  

			</div>

			<div class="item-promotion-warp" v-if="goods['tag_is_show'] === 1"> 
			    <span class="item-promotion">{{goods['tag']}} &nbsp;</span>
			</div> 
			
			<div class="astrict-warp" v-if="goods['astrict'] !== ''">{{ goods['astrict'] }}
			</div>
	
			<div class="treats-info-data">

				<div class="treats-info-data-date">Expiration Date: <span style="color: #233333;">{{ formatDate(goods['end_date']) }}</span>
				</div>
			</div>

		</div>
		<div class="question">
			Redeem this reward?
		</div>
		<div class="buttons">
			<div v-on:click.stop="closeprompt">No</div>
			<div v-on:click.stop="exchange">Yes</div>
		</div>
	</div>

	<div class="prompt" v-if="prompt2" v-bind:class="{'prompt2Height' : prompt2 , 'prompt1Height' : !prompt2}">
		<div class="prompt2-cover">
			<img v-bind:src="treat_logo" alt="">
		</div>
		<div class="prompt2-foo"> 
			<div class="prompt2-tip">
				<div class="prompt2-tip-title">Successfully Redeemed!</div>
				<div class="prompt2-tip-info">We have send you an email with the <br> coupon and purchase link.</div>
			</div>
			<div class="prompt2-button">
				<div  v-on:click.stop="closeprompt('success')">Got it</div>
			</div>
		</div>
	</div>

	<div class="prompt" v-if="prompt3" v-bind:class="{'prompt2Height' : prompt2 , 'prompt1Height' : !prompt2}">
		<div class="prompt3-cover">
			<img v-bind:src="error_logo" alt="">
		</div>
		<div class="prompt2-foo"> 
			<div class="prompt2-tip">
				<div class="prompt2-tip-title">Error</div>
				<div class="prompt2-tip-info">Please try again.</div>
			</div>
			<div class="prompt3-button">
				<div v-on:click.stop="closeprompt('tryagain')">OK</div>
			</div>
		</div>
	</div>

</div>
</template>

<script>
	import publicConf from '../common/public.conf.js';
	import common from '../common/common.js';
	import API from '../common/public.api.js'; 

	const baseUrl = publicConf.currentBaseUrl;
	
	export default { 
		props : ['goods'],
		data () {
			return {
				isCanBeExchanged : true,
				formatDate : common.formatDate,
				load_img : '',
				treat_icon : baseUrl + "treat-icon.png",
				treat_logo : baseUrl + "treat-logo.png",
				error_logo : baseUrl + "error-logo.png",
				default_product : "data:image/png;base64,",
				productCover : baseUrl + "pr02.png",
				promotionCode : '',
				prompt1 : true,
				prompt2 : false,
				prompt3 : false
			}
		},

		mounted () {

		},

		methods : {
			cutoffStr (str) { 
				if ( str.length >= 85 ) { 
					return str.substring(0,85) + "...";
				} else {
					return str;
				}
			},

			closeprompt (info) {  
				let item = {
					 productId : this.goods['product-id'],
					 promotionCode : this.promotionCode
				}
				this.$emit("closeprompt",info,item);
				this.isCanBeExchanged = true;  
			},

			each (str) {
				var strArr = str.split(" ");
				if (strArr[1] === 'each' ) {
					return strArr[0] + "&nbsp;<span style='font-size:12px;'>each</span>"
				} else {
					return str;
				}
			},

			exchange() {
				console.log(this.isCanBeExchanged);
				if ( this.isCanBeExchanged === true ) {
					this.isCanBeExchanged = false;
					let me = this; 
					common.request({ 
					    url : API.TREATS.POST_REDEEM ,
					    type : 'POST',
					    data : { 
					    	'pid' : me.goods.pid,
					    	'user_id' : common.GET()['userId'], 
					    	'treats_worth' : me.goods.treats_worth 
					    },
					    error :  function (req,info) {
					    	me.prompt1 = false;
					    	me.prompt2 = false;
					    	me.prompt3 = true; 
					    }
					},function (data) {
						
					    if ( data.code === 0 && data.msg === "ok" ) { 
					    	me.promotionCode = data.data.promotion_code;
					    	me.prompt1 = false;
					    	me.prompt3 = false;
					    	me.prompt2 = true;   
					    } else {
					    	me.prompt1 = false;
					    	me.prompt2 = false;
					    	me.prompt3 = true;  
					    } 
					}); 
				} else {
					console.log('不可以反复兑换。');
				}
			},
			getGoodUrl(goods){
			    let goodUrl = "";
			    if(goods['image-url'].length>0){
			        let arrUrl = goods['image-url'].split(",");
			        if(arrUrl.length>0){
                        goodUrl = arrUrl[0];
					}
				}
				return goodUrl;
			}
		}
	}

</script> 

